/// Integration test: session resume (opt-in)
pub fn integration__session_resume_test() {
  case integration_enabled("integration__session_resume_test") {
    True -> {
      case is_authenticated() {
        True -> {
          // 1. Create initial session
          let opts1 = QueryOptions(..default_options(), max_turns: Some(1))
          let assert Ok(stream1) = query("Remember the number 42", opts1)

          // 2. Consume stream to get session_id from SystemMessage
          let session_id = extract_session_id(stream1)

          // 3. Resume session with follow-up
          let opts2 = QueryOptions(
            ..default_options(),
            resume_session_id: Some(session_id),
            max_turns: Some(1)
          )
          let assert Ok(stream2) = query("What number did I ask you to remember?", opts2)

          // 4. Verify response references the number
          // (This tests that session context is preserved)
        }
        False -> {
          io.println("[SKIP:AUTH] Auth not available")
          should.be_true(True)
        }
      }
    }
    False -> should.be_true(True)  // Skipped
  }
}
```

