  CanUseToolContext(tool_name: String, tool_input: Dynamic, session_id: String, permission_suggestions: List(String), blocked_path: Option(String))
}

pub type UserPromptSubmitContext {
  UserPromptSubmitContext(prompt: String, session_id: String)
}

pub type StopContext {
  StopContext(reason: String, session_id: String)
}

pub type SubagentStopContext {
  SubagentStopContext(subagent_id: String, reason: String, session_id: String)
}

pub type PreCompactContext {
  PreCompactContext(session_id: String)
}
```

### Result Types
```gleam
