{"type":"control_request","request_id":"req_0","request":{"subtype":"initialize","hooks":{"PreToolUse":[{"matcher":null,"hookCallbackIds":["hook_0"]}]},"mcp_servers":["my-server"],"enable_file_checkpointing":true}}

